public class D {
    public void start() {
        System.out.println("Vehicle is starting...");
    }
}

public class Car extends D {
    public void drive() {
        System.out.println("Car is driving...");
    }
}
public class main {
    public static void main(String[] args) {
        Car myCar = new Car();
        myCar.start(); // Inherited from Vehicle
        myCar.drive(); // Defined in Car
    }
}
